        view.addSubview(buyCoinsButton)

        coinLabel.translatesAutoresizingMaskIntoConstraints = false
        collectCoinButton.translatesAutoresizingMaskIntoConstraints = false
        buyCoinsButton.translatesAutoresizingMaskIntoConstraints = false

        NSLayoutConstraint.activate([
            coinLabel.topAnchor.constraint(equalTo: view.safeAreaLayoutGuide.topAnchor, constant: 20),
            coinLabel.leadingAnchor.constraint(equalTo: view.leadingAnchor, constant: 20),
            coinLabel.trailingAnchor.constraint(equalTo: view.trailingAnchor, constant: -20),

            collectCoinButton.topAnchor.constraint(equalTo: coinLabel.bottomAnchor, constant: 20),
            collectCoinButton.centerXAnchor.constraint(equalTo: view.centerXAnchor),

            buyCoinsButton.topAnchor.constraint(equalTo: collectCoinButton.bottomAnchor, constant: 20),
            buyCoinsButton.centerXAnchor.constraint(equalTo: view.centerXAnchor)
        ])
    }

    @objc func collectCoin() {
        gameLogic.collectCoin()
        updateUI()
    }

    @objc func buyCoins() {
        gameLogic.buyCoins()
        updateUI()
    }

    func updateUI() {
        coinLabel.text = "Coins: \(gameLogic.playerCoins)"
    }
}

class GameLogic {
    var playerCoins: Int = 0
    var playerTreasures: [String] = []

    func collectCoin() {
        playerCoins += 1
    }

    func collectTreasure(_ treasure: String) {
        playerTreasures.append(treasure)
    }

    func buyCoins() {
        // In-app purchase logic
        playerCoins += 100
    }
}
